import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar/Sidebar.js";
import Navbar from "../components/NavBar/Navbar.js";
import PerfectScrollbar from "perfect-scrollbar";
import { makeStyles } from "@material-ui/core/styles";
import StreamPlayer from "../components/Stream/StreamPlayer.js";
import styles from "../assets/jss/material-dashboard-react/layouts/adminStyle.js";
import InfoContainer from "./Stream/InfoContainer.js";
import { useHistory } from "react-router-dom";
import KakaoMap from "../components/KakaoMap/KakaoMap.js";

let ps;

function MainContainer({ match }) {
  const [mobileOpen, setMobileOpen] = useState(false);
  const mainPanel = React.createRef();
  const useStyles = makeStyles(styles);
  const history = useHistory();
  const classes = useStyles();
  console.log(match.path);
  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const userInfo = JSON.parse(localStorage.getItem("user"));

  const resizeFunction = () => {
    if (window.innerWidth >= 960) {
      setMobileOpen(false);
    }
  };

  const [streamUrl, setStreamUrl] = useState(null);
  const [hlsUrl, setHlsUrl] = useState(null);

  const onStream = e => {
    e.preventDefault();
    const target = e.target.dataset.index;
    const hlsTarget = e.target.dataset.hls;
    const isActive = e.target.dataset.active;
    console.log(e);
    if (isActive === "true") {
      setStreamUrl(target);
      setHlsUrl(hlsTarget);

      history.push("/stream");
    } else {
    }
  };

  useEffect(() => {
    if (navigator.platform.indexOf("Win") > -1) {
      ps = new PerfectScrollbar(mainPanel.current, {
        suppressScrollX: true,
        suppressScrollY: false
      });
      document.body.style.overflow = "hidden";
    }
    window.addEventListener("resize", resizeFunction);
    // Specify how to clean up after this effect:
    return function cleanup() {
      if (navigator.platform.indexOf("Win") > -1) {
        ps.destroy();
      }
      window.removeEventListener("resize", resizeFunction);
    };
  }, [mainPanel]);

  return (
    <div className={classes.wrapper}>
      <Sidebar
        userInfo={userInfo}
        handleDrawerToggle={handleDrawerToggle}
        open={mobileOpen}
        color="red"
      />
      <div className={classes.mainPanel} ref={mainPanel}>
        <Navbar handleDrawerToggle={handleDrawerToggle} />
        <div className={classes.content}>
          {match.path === "/stream" ? (
            <>
              <KakaoMap></KakaoMap>
              <StreamPlayer streamUrl={streamUrl}></StreamPlayer>
              <StreamPlayer streamUrl={hlsUrl}></StreamPlayer>
            </>
          ) : (
            <InfoContainer onStream={onStream}></InfoContainer>
          )}
        </div>
      </div>
    </div>
  );
}

export default MainContainer;
