import React from "react";

import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper
} from "@material-ui/core";

import { MdPowerSettingsNew, MdHighlightOff, MdHistory } from "react-icons/md";
import { makeStyles } from "@material-ui/core/styles";
import CardHeader from "../Common/CardHeader.js";

// import tableStyle from "../../assets/jss/material-dashboard-react/components/tableStyle.js";
import tableStyle from "../../assets/jss/material-dashboard-react/components/tableStyle.js";
import styled from "styled-components";
import LoadingSpinner from "../Common/LoadingSpinner.js";
const useStyles = makeStyles(tableStyle);

const styles = {
  cardCategoryWhite: {
    "&,& a,& a:hover,& a:focus": {
      color: "#525151",
      margin: "0",
      fontSize: "14px",
      marginTop: "0",
      marginBottom: "0"
    },
    "& a,& a:hover,& a:focus": {
      color: "#525151"
    }
  },
  cardTitleWhite: {
    color: "#000000",
    fontWeight: "400",
    marginTop: "0px",
    minHeight: "auto",
    marginBottom: "1rem",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    // marginBottom: "0.5rem",
    textDecoration: "none",
    "& small": {
      color: "#777",
      fontSize: "65%",
      fontWeight: "400",
      lineHeight: "1"
    }
  }
};

const cardStyleds = makeStyles(styles);

function StreamInfo({ info, loading, onRefresh, onStream }) {
  const classes = useStyles();
  const cardClasses = cardStyleds();
  return (
    <>
      <CardHeader color="warning">
        <h4 className={cardClasses.cardTitleWhite}>스트리밍 정보</h4>
        <p className={cardClasses.cardCategoryWhite}>
          현제 스트리밍 되는 카메라를 확인할 수 있습니다.
        </p>
      </CardHeader>
      <div
        style={{
          textAlign: "right",
          marginTop: "24px",
          paddingRight: "1rem"
        }}
      >
        <MdHistory
          fontSize="2rem"
          color="green"
          cursor="pointer"
          onClick={onRefresh}
        ></MdHistory>
      </div>
      {loading ? (
        <LoadingSpinner></LoadingSpinner>
      ) : (
        <TableContainer className={classes.tableResponsive} component={Paper}>
          <Table className={classes.table}>
            <TableHead className={classes.warningTableHeader}>
              <TableRow className={classes.tableHeadRow}>
                <TableCell
                  className={classes.tableCell + " " + classes.tableHeadCell}
                  align="center"
                  style={{ fontWeight: 400, color: "black" }}
                >
                  방송
                </TableCell>
                <TableCell
                  className={classes.tableCell + " " + classes.tableHeadCell}
                  align="center"
                  style={{ fontWeight: 400, color: "black" }}
                >
                  위치
                </TableCell>
                <TableCell
                  className={classes.tableCell + " " + classes.tableHeadCell}
                  align="center"
                  style={{ fontWeight: 400, color: "black" }}
                >
                  시작/종료 시간
                </TableCell>
                <TableCell
                  className={classes.tableCell + " " + classes.tableHeadCell}
                  align="center"
                  style={{ fontWeight: 400, color: "black" }}
                >
                  담당자
                </TableCell>
              </TableRow>
            </TableHead>

            <TableBody>
              {info &&
                info.map(item => (
                  <TableRow
                    key={item.target.mac}
                    className={classes.tableBodyRow}
                    data-index={item.hls_url}
                    data-active={item.isActive}
                    onClick={onStream}
                    style={{ cursor: "pointer" }}
                  >
                    <TableCell
                      className={classes.tableCell}
                      data-index={item.hls_url}
                      data-active={item.isActive}
                      component="th"
                      scope="row"
                      align="center"
                      style={{ fontSize: "1.5rem" }}
                    >
                      {item.isActive ? (
                        <MdPowerSettingsNew
                          fontSize="1.5rem"
                          color="green"
                        ></MdPowerSettingsNew>
                      ) : (
                        <MdHighlightOff color="red"></MdHighlightOff>
                      )}
                    </TableCell>
                    <TableCell
                      data-index={item.hls_url}
                      data-active={item.isActive}
                      className={classes.tableCell}
                      align="center"
                      style={{ fontSize: "1rem" }}
                    >
                      {item.location ? item.location : "X"}
                    </TableCell>
                    {item.isActive === false && (
                      <TableCell
                        data-index={item.hls_url}
                        data-active={item.isActive}
                        className={classes.tableCell}
                        align="center"
                        style={{ fontSize: "1rem" }}
                      >
                        {item.finish_date ? item.finish_date : "X"}
                      </TableCell>
                    )}
                    {item.isActive === true && (
                      <TableCell
                        data-index={item.hls_url}
                        data-active={item.isActive}
                        className={classes.tableCell}
                        align="center"
                        style={{ fontSize: "1rem" }}
                      >
                        {item.start_date ? item.start_date : "X"}
                      </TableCell>
                    )}

                    <TableCell
                      data-index={item.hls_url}
                      data-active={item.isActive}
                      className={classes.tableCell}
                      align="center"
                      style={{ fontSize: "1rem" }}
                    >
                      {item.target.name ? item.target.name : "X"}
                    </TableCell>
                  </TableRow>
                ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </>
  );
}

export default StreamInfo;
