import React from "react";
import styled from "styled-components";
import CardHeader from "../Common/CardHeader";
import SubNavComponent from "../Common/SubNavComponent";
import SubNavContainer from "../Common/SubNavContainer";
import Canvas from "./Canvas";
import { makeStyles } from "@material-ui/core/styles";

const styles = {
  cardCategoryWhite: {
    "&,& a,& a:hover,& a:focus": {
      color: "#525151",
      margin: "0",
      fontSize: "14px",
      marginTop: "0",
      marginBottom: "0"
    },
    "& a,& a:hover,& a:focus": {
      color: "#525151"
    }
  },
  cardTitleWhite: {
    color: "#000000",
    fontWeight: "400",
    marginTop: "0px",
    minHeight: "auto",
    marginBottom: "1rem",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    // marginBottom: "0.5rem",
    textDecoration: "none",
    "& small": {
      color: "#777",
      fontSize: "65%",
      fontWeight: "400",
      lineHeight: "1"
    }
  }
};
const cardStyleds = makeStyles(styles);

const DetectImage = styled.img`
  width: calc(100% - 30px);

  position: absolute;
`;

function DetectImager({ targetImage, onReload }) {
  const cardClasses = cardStyleds();
  const initialState = [];
  targetImage &&
    targetImage.target_detection.map(item =>
      initialState.push({
        id: item.pk,
        type: item.detectType,
        x: item.xmin,
        y: item.ymin,
        width: item.xmax - item.xmin,
        height: item.ymax - item.ymin
      })
    );

  return (
    <>
      <CardHeader color="warning">
        <h4 className={cardClasses.cardTitleWhite}>
          일시 : {targetImage && targetImage.createAt}
        </h4>
        <p className={cardClasses.cardCategoryWhite}>
          장비명 : {targetImage && targetImage.target.name}
        </p>
        <p className={cardClasses.cardCategoryWhite}>위치 : X</p>
      </CardHeader>
      <SubNavContainer>
        <SubNavComponent onClick={onReload}>갱신</SubNavComponent>
        <SubNavComponent>영상 변경</SubNavComponent>
      </SubNavContainer>
      {targetImage && targetImage.path && initialState !== [] && (
        <>
          <DetectImage
            id="target-img"
            src={"http://www.fireban.kr/" + targetImage.path}
            alt="detect"
            // onLoad={onImgLoad}
          ></DetectImage>
          <Canvas initialState={initialState}></Canvas>
        </>
      )}
    </>
  );
}

export default DetectImager;
