import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import styles from "../assets/jss/material-dashboard-react/layouts/adminStyle.js";
import SidebarContainer from "./SidebarContainer.js";
import NavbarContainer from "./NavbarContainer.js";
import StreamPlayer from "../components/Stream/StreamPlayer.js";
import KakaoMap from "../components/KakaoMap/KakaoMap.js";
import { useHistory } from "react-router-dom";
import styled, { css } from "styled-components";
import { useDispatch, useSelector } from "react-redux";
import { changeGpsTarget, getGpsInfo } from "../store/modules/stream.js";
const mainPanel = React.createRef();

const VideoButton = styled.div`
  height: 2rem;
  cursor: pointer;
  color: #fff;
  text-align: center;
  width: auto;
  margin: 10px 15px 0;
  display: block;
  padding: 10px 15px;
  position: absolute;
  z-index: 10;
  transition: all 300ms linear;
  font-family: "Roboto", "Helvetica", "Arial", sans-serif;
  font-weight: 300;
  line-height: 1.5em;
  border-radius: 3px;
  background-color: #f44336;
  box-shadow: 0 12px 20px -10px rgba(244, 67, 54, 0.28),
    0 4px 20px 0 rgba(244, 67, 54, 0.12), 0 7px 8px -5px rgba(244, 67, 54, 0.2);
  &:hover {
    box-shadow: 0 12px 20px -10px rgba(244, 67, 54, 0.28),
      0 4px 20px 0 rgba(244, 67, 54, 0.12),
      0 7px 8px -5px rgba(244, 67, 54, 0.2);
  }
  &:focus {
    box-shadow: 0 12px 20px -10px rgba(244, 67, 54, 0.28),
      0 4px 20px 0 rgba(244, 67, 54, 0.12),
      0 7px 8px -5px rgba(244, 67, 54, 0.2);
  }
`;

const VideoText = styled.div`
  color: #fff;
  margin: 0;
  font-size: 24px;
  font-family: "Roboto", "Helvetica", "Arial", sans-serif;
  font-weight: 500;
  line-height: 30px;
`;

const VideoContainer = styled.div`
  ${props =>
    props.visible
      ? css`
          display: inline;
        `
      : css`
          display: none;
        `};
`;

function StreamContainer({ match }) {
  const dispatch = useDispatch();
  const target = useSelector(state => state.stream.target);
  const gpsInfo = useSelector(state => state.stream.gpsInfo);
  const [isTic, setIsTic] = useState(true);
  const history = useHistory();
  const key = match.params.key;

  useEffect(() => {
    if (target !== key) {
      console.log(target, key);
      dispatch(changeGpsTarget(key));
      const timer = setInterval(() => dispatch(getGpsInfo(key)), 5000);
    }
  }, [dispatch, key, target]);

  if (!key) {
    // 통신 안되면 뒤로 가기
    history.push("/");
  }

  const ticStream = "/api/stream/tic/" + key + "/index.m3u8";
  const hlsStream = "/api/stream/live/" + key + "/index.m3u8";

  const useStyles = makeStyles(styles);
  const classes = useStyles();

  const onChangeSteam = e => {
    dispatch(getGpsInfo(key));
    setIsTic(!isTic);
  };

  return (
    <div className={classes.wrapper}>
      <SidebarContainer></SidebarContainer>
      <div className={classes.mainPanel} ref={mainPanel}>
        <NavbarContainer />
        <div className={classes.content}>
          <KakaoMap
            cordinateX={
              gpsInfo && gpsInfo.cordinateX ? gpsInfo.cordinateX : "37.504234"
            }
            cordinateY={
              gpsInfo && gpsInfo.cordinateY ? gpsInfo.cordinateY : "127.047812"
            }
          ></KakaoMap>
          <VideoContainer visible={isTic}>
            <VideoButton onClick={onChangeSteam}>
              <VideoText>영상 변경</VideoText>
            </VideoButton>
            <StreamPlayer streamUrl={ticStream}></StreamPlayer>
          </VideoContainer>
          <VideoContainer visible={!isTic}>
            <VideoButton onClick={onChangeSteam}>
              <VideoText>영상 변경</VideoText>
            </VideoButton>
            <StreamPlayer streamUrl={hlsStream}></StreamPlayer>
          </VideoContainer>
        </div>
      </div>
    </div>
  );
}

export default StreamContainer;
