import React, { useCallback } from "react";
import { makeStyles } from "@material-ui/core/styles";
import styles from "../assets/jss/material-dashboard-react/layouts/adminStyle.js";
import InfoContainer from "./Record/InfoContainer.js";
import { useHistory } from "react-router-dom";
import SidebarContainer from "./SidebarContainer.js";
import NavbarContainer from "./NavbarContainer.js";

function RecordContainer({ match }) {
  const mainPanel = React.createRef();
  const useStyles = makeStyles(styles);
  const classes = useStyles();

  const history = useHistory();

  const onStream = useCallback(
    //submit
    async e => {
      e.preventDefault();
      try {
        const target = e.currentTarget.dataset.index;

        history.push("/video/" + target);
      } catch (e) {
        alert("잠시 후 다시 시도해주세요.");
      }
    },
    [history]
  );

  return (
    <div className={classes.wrapper}>
      <SidebarContainer></SidebarContainer>
      <div className={classes.mainPanel} ref={mainPanel}>
        <NavbarContainer />
        <div className={classes.content}>
          <InfoContainer onStream={onStream}></InfoContainer>
        </div>
      </div>
    </div>
  );
}

export default RecordContainer;
